                            );
        ContractFund[name]= address(new FundContract(ContractName,i_owner,TokenAddress,Duration,priceFeed,FundingGoal,mainContractAddress));
        _transfer(address(this),address(NewFund[count].Name),value);
        count=count+1;
    }


}

